<?phpsession_start(); $u = isset($_POST['u'])? $_POST['u'] : 0;$p = isset($_POST['p'])? $_POST['p'] : 0;if (!$u || !$p){	 echo json_encode(			array(				'success'=>false,				'title'=>'Gagal Login',				'msg'=>'Silahkan di isi Field User atau Password!'				)			);}else{	include_once ("include/database.php");	try {		$sql = "select `userid`,`nama` from `m_user` WHERE userid=:u AND userpass=:p";		$query = $db->prepare($sql);		$query->bindParam(':u', $u, \PDO::PARAM_INT);		$query->bindParam(':p', $p, \PDO::PARAM_STR);		$query->execute();		if ($query->fetch(PDO::FETCH_ASSOC) > 0)		{			while($data = $query->fetch())			{				echo json_encode($data);			}		}		else		{			echo json_encode(				array(					'success'=>false,					'title'=>'Gagal Login',					'msg'=>'Username atau Password salah!'				)			);		}	}	catch (PDOException $e) {		 echo json_encode(			array(				'success'=>false,				'title'=>'Gagal Login',				'msg'=>'Username atau Password salah!'				)			);	}	}