<?phpsession_start(); $u = isset($_POST['u'])? $_POST['u'] : 0;$p = isset($_POST['p'])? md5($_POST['p']) : 0;if (!$u || !$p){	 echo json_encode(			array(				'success'=>false,				'title'=>'Gagal Login',				'msg'=>'Silahkan di isi Field User atau Password!'				)			);}else{	include ("include/database.php");	$query = mysql_query("select count(nama) as jml from m_user where userid='$u' and userpass='$p' and active='1'");	$hasil = mysql_fetch_assoc($query);		if($hasil['jml'] > 0){		$query = mysql_query("select userid,nama,role from m_user where userid='$u'");		$result = mysql_fetch_assoc($query);		$_SESSION['auth']=$result;		echo json_encode(			array(				'success'=>true				)			);				}	else {		echo json_encode(			array(				'success'=>false,				'title'=>'Gagal Login',				'msg'=>'User atau Password salah'				)			);	}				}